import React, { Component } from "react";
import "./index.css";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tasks: "",
      taskList: [],
    };
  }
  componentDidMount = () => {
    const tasks = localStorage.getItem("tasks");
    if (tasks !== '') {
      const taskArry = JSON.parse(tasks);
      this.setState({ taskList: taskArry });
    }
  };

  handleTask = (event) => {
    this.setState({
      task: event.target.value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const { taskList, tasks } = this.state;
    // const newTask = { id: taskList.length, desc: task };
    const newTasks = [...taskList, tasks];
    localStorage.setItem("tasks", JSON.stringify(newTasks));
    this.setState({ taskList: newTasks, task: "" });

    // this.setState({taskList:newTasks,task:""});
  };

  clearAll = () => {
    this.setState({ taskList: [] });
    localStorage.setItem("tasks", []);
    // localStorage.removeItem("tasks");
  };

  render() {
    return (
      <>
        <form onSubmit={this.handleSubmit}>
          <div className="total">
            <div className="heading">
              <h1>Daily To-Do List Manager</h1>
            </div>

            <div className="show">
              <p>Wednesday</p>
              <p>07-10-2020</p>
              <input
                type="text"
                placeholder="Write your the Task"
                value={this.state.task}
                onChange={this.handleTask}
              />
              <button type="submit">+</button>
              <br />
              <ol className="display">
                {this.state.taskList.map((task, i) => {
                  console.log(task);
                  return (
                    <li className="design" key={i}>
                      {task}
                    </li>
                  );
                })}
              </ol>
              <button type={"button"} onClick={this.clearAll}>
                Clear All
              </button>
            </div>
          </div>
        </form>
      </>
    );
  }
}

export default App;
